  [30222]={BaseID=30222,Prefix="杂物",Postfix="绿",ItemName="梨花",ItemDesc="初春开花，花色洁白，如同雪花，花香浓烈。有诗云：粉淡香清自一家，未容桃李占年华。常思南郑清明路，醉袖迎风雪一杈。",Rank=RankType.RT_Green,Icon="ItemIcon/caoyao/icon_eauip_caoyao_0005",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialFlower,LimitNum=9999,BuyPrice=180,SellPrice=15,Present=FavorType.FT_Flower,Value1="",ItemSource="黑月崖后山、武当山路",},
  [30231]={BaseID=30231,Prefix="杂物",Postfix="蓝",ItemName="鸢尾",ItemDesc="又名蓝蝴蝶、扁竹花等，花香气淡雅，其根状茎可作中药，全年可采，具有清热祛火作用。",Rank=RankType.RT_Blue,Icon="ItemIcon/zawu/icon_eauip_zawu_0018",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialFlower,LimitNum=9999,BuyPrice=420,SellPrice=40,Present=FavorType.FT_Flower,Value1="",ItemSource="太湖湖岸、泰山小径、广州商店",},
  [30232]={BaseID=30232,Prefix="杂物",Postfix="蓝",ItemName="菊花",ItemDesc="花中四君子之一，最早的记载见之于《周官》、《埠雅》，栽培菊花最初是以食用和药用为目的，后逐渐演变为赏菊的雅趣。历来有重阳赏菊和饮菊花酒的习俗，唐代孟浩然有诗云：待到重阳日，还来就菊花。",Rank=RankType.RT_Blue,Icon="ItemIcon/caoyao/icon_eauip_caoyao_0004",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialFlower,LimitNum=9999,BuyPrice=420,SellPrice=40,Present=FavorType.FT_Flower,Value1="",ItemSource="大理城郊、武当山路",},
  [30241]={BaseID=30241,Prefix="杂物",Postfix="紫",ItemName="童子面",ItemDesc="形姿优美，叶浓绿而光泽，花白色而带红晕，花形艳丽缤纷。极具观赏价值，极为珍贵。",Rank=RankType.RT_Purple,Icon="ItemIcon/caoyao/icon_eauip_caoyao_0004",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialFlower,LimitNum=9999,BuyPrice=880,SellPrice=85,Present=FavorType.FT_Flower,Value1="",ItemSource="恒山密林、开封商店",},
  [30242]={BaseID=30242,Prefix="杂物",Postfix="紫",ItemName="桂花",ItemDesc="宋之问的《灵隐寺》诗中有“桂子月中落，天香云外飘”的著名诗句，故后人亦称桂花为“天香”。",Rank=RankType.RT_Purple,Icon="ItemIcon/zawu/icon_eauip_zawu_0018",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialFlower,LimitNum=9999,BuyPrice=880,SellPrice=85,Present=FavorType.FT_Flower,Value1="",ItemSource="黑月崖后山",},
