  [30371]={BaseID=30371,Prefix="杂物",Postfix="暗金",ItemName="露华百英粉",ItemDesc="一种高级的爽身粉，制作工艺繁琐、香气袭人，长期用可以保养肌肤，据说汉成帝宠妃赵合德最爱在出浴后用露华百英粉，以致汉成帝认为赵合德体自香也",Rank=RankType.RT_DarkGolden,Icon="ItemIcon/zawu/icon_eauip_zawu_0016",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialMaquillage,LimitNum=9999,BuyPrice=16000,SellPrice=1600,Present=FavorType.FT_Maquillage,Value1="",ItemSource="开封商店",},
