  [30412]={BaseID=30412,Prefix="杂物",Postfix="白",ItemName="梨花酒",ItemDesc="十分常见的酒类，成品外观呈澄清透明的液体，完整的梨花悬浮于透明酒液中，其内在质量既保留了传统的白酒成分，又增添了梨花特有的香型，品尝时可谓赏心悦目。",Rank=RankType.RT_White,Icon="ItemIcon/zawu/icon_eauip_zawu_001a",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialWine,LimitNum=9999,BuyPrice=50,SellPrice=10,Present=FavorType.FT_Wine,Value1="",ItemSource="峨嵋秘境、开封商店",},
