  [30415]={BaseID=30415,Prefix="杂物",Postfix="绿",ItemName="酒葫芦",ItemDesc="用来做酒壶的葫芦必须是要是天然长成的，完全成熟的酒葫芦，皮质厚实、紧凑、结实耐用，头一个结出的葫芦最佳。",Rank=RankType.RT_Green,Icon="ItemIcon/zawu/icon_eauip_zawu_0082",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialWine,LimitNum=9999,BuyPrice=160,SellPrice=15,Present=FavorType.FT_Wine,Value1="",ItemSource="京城商店",},
  [30421]={BaseID=30421,Prefix="杂物",Postfix="绿",ItemName="汾酒",ItemDesc="作为宫廷御酒受到北齐武成帝的极力推崇，被载入二十四史，原产于汾阳杏花村，因此民间常称之杏花村酒。唐代杜牧有诗云：借问酒家何处有，牧童遥指杏花村。",Rank=RankType.RT_Green,Icon="ItemIcon/zawu/icon_eauip_zawu_001b",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialWine,LimitNum=9999,BuyPrice=160,SellPrice=15,Present=FavorType.FT_Wine,Value1="",ItemSource="杭州城郊、大理城郊、杭州商店",},
  [30422]={BaseID=30422,Prefix="杂物",Postfix="绿",ItemName="高粱酒",ItemDesc="盐都以高粱酿造白酒，历来独步天下，久享盛名。南宋陆游滴居盐都时，就赞有诗曰：鹅黄名酿何由得，且醉杯中琥珀红。",Rank=RankType.RT_Green,Icon="ItemIcon/zawu/icon_eauip_zawu_001b",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialWine,LimitNum=9999,BuyPrice=160,SellPrice=15,Present=FavorType.FT_Wine,Value1="",ItemSource="黑月崖后山、京城商店",},
