  [30432]={BaseID=30432,Prefix="杂物",Postfix="蓝",ItemName="葡萄酒",ItemDesc="古时亦作蒲陶酒，酒色呈自然深宝石红，宛如鲜血，饮第一口酒，酒液经过喉头时，平顺醇厚，令人神清气爽。早在太史公的《史记·大宛列传》中就有记载：宛左右以蒲桃为酒，富人藏酒至万余石、久者数十年不败。",Rank=RankType.RT_Blue,Icon="ItemIcon/zawu/icon_eauip_zawu_001a",ItemImg="",ItemType=ItemTypeDetail.ItemType_MaterialWine,LimitNum=9999,BuyPrice=380,SellPrice=35,Present=FavorType.FT_Wine,Value1="",ItemSource="峨嵋秘境、大理商店",},
