  [70703]={BaseID=70703,Prefix="",Postfix="暗金",ItemName="家常豆腐",ItemDesc="以豆腐为主材，酱油、辣椒为调料，有肉加肉，有菜加菜，营养丰富味道好还下饭，是广泛流行的一道家常菜。\n在剧本中使用：增加一名队友5点拳掌精通",Rank=RankType.RT_DarkGolden,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0022",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=8000,SellPrice=800,Present=FavorType.FT_Food,EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_QUANZHANGJINGTONG,Value1="5",Value2="",Value3="",Value4="",Value5="",ItemSource="广州商店",},
  [70704]={BaseID=70704,Prefix="",Postfix="暗金",ItemName="酿豆腐",ItemDesc="是中国的传统菜式，常见于湖南、广东、广西、福建、四川等地区。口味清淡，汤汁醇厚，鲜嫩滑润，口味鲜美，广受欢迎。\n在剧本中使用：增加一名队友5点刀法精通",Rank=RankType.RT_DarkGolden,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0011",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=8000,SellPrice=800,Present=FavorType.FT_Food,EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_DAOFAJINGTONG,Value1="5",Value2="",Value3="",Value4="",Value5="",ItemSource="",},
