  [70711]={BaseID=70711,Prefix="",Postfix="橙",ItemName="甜粽子",ItemDesc="属于甜党的粽子，口感清甜，入齿留香！在端午时节交给自己的徒儿能提升徒儿的能力。\n在剧本中使用：增加一名队友490点生命值",Rank=RankType.RT_Orange,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0010",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=3500,SellPrice=350,Present=FavorType.FT_Food,Feature={ItemFeature.IF_CantDrop,},EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_MAXHP,Value1="490",Value2="",Value3="",Value4="",Value5="",ItemSource="",},
  [70712]={BaseID=70712,Prefix="",Postfix="橙",ItemName="咸粽子",ItemDesc="属于咸党的粽子，咸蛋软糯，猪肉鲜嫩！在端午时节交给自己的徒儿能提升徒儿的能力。\n在剧本中使用：增加一名队友410点真气",Rank=RankType.RT_Orange,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0010",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=3500,SellPrice=350,Present=FavorType.FT_Food,Feature={ItemFeature.IF_CantDrop,},EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_MAXMP,Value1="410",Value2="",Value3="",Value4="",Value5="",ItemSource="",},
