  [70716]={BaseID=70716,Prefix="",Postfix="橙",ItemName="汤圆",ItemDesc="用黑芝麻、猪板油做馅、加入少许白糖，外面用糯米粉搓成圆形，煮熟后，吃起来香甜软糯，回味无穷。在元宵时节交给自己的徒儿能提升徒儿的能力。\n在剧本中使用：增加一名队友45点防御",Rank=RankType.RT_Orange,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0010",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=3500,SellPrice=350,Present=FavorType.FT_Food,Feature={ItemFeature.IF_CantDrop,},EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_DEF,Value1="45",Value2="",Value3="",Value4="",Value5="",ItemSource="",},
