  [70718]={BaseID=70718,Prefix="",Postfix="橙",ItemName="青团",ItemDesc="江南地区的传统特色小吃，青色，用艾草的汁拌进糯米粉里，再包裹进豆沙馅儿或者莲蓉，不甜不腻，带有清淡却悠长的清香。在清明时节交给自己的徒儿能提升徒儿的能力。\n在剧本中使用：增加一名队友120点武学攻击",Rank=RankType.RT_Orange,Icon="ItemIcon/shiwu/icon_eauip_shiwu_0010",ItemType=ItemTypeDetail.ItemType_Dish,LimitNum=9999,BuyPrice=3500,SellPrice=350,Present=FavorType.FT_Food,Feature={ItemFeature.IF_CantDrop,},EffectType=EffectType.ETT_ModRoleProperties,EffectTraget="",RoleAttribute=AttrType.ATTR_MARTIAL_ATK,Value1="120",Value2="",Value3="",Value4="",Value5="",ItemSource="",},
